import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/auth';
{/* <Route path='/inventory' component={() => window.location = './inventory.html'}/> */}

const Navbar = ({ auth: { isAuthenticated, loading }, logout }) => {
  const authLinks = (
    <ul>

      {/* <li>
      <a href="/inventory"> FAQ </a>

      </li>

      */}

      <li>
        <Link to='/technology'>Foglio Technology</Link>
      </li>

      <li>
        <Link to='/profiles'>Designers</Link>
      </li>
      <li>
        <Link to='/posts'>News</Link>
      </li>
      <li>
        <Link to='/dashboard'>
        <i class="fas fa-shopping-bag"></i>{' '}
          <span className='hide-sm'>Dashboard</span>
        </Link>
      </li>
      <li>
        <a onClick={logout} href='#!'>
         {' '}
          <span className='hide-sm'>Logout</span>
        </a>
      </li>
    </ul>
  );

  const guestLinks = (
    <ul>
      <li>
        <Link to='/technology'>Foglio Technology</Link>
      </li>
      <li>
        <Link to='/register'>Register</Link>
      </li>
      <li>
        <Link to='/login'>Login</Link>
      </li>
    </ul>
  );



  return (
    <nav className='navbar bg-dark'>
      <h1>
        <Link to='/'>
        <img src={require('./foglio.png')} alt="fireSpot"/>
        </Link>
      </h1>
      {!loading && (
        <Fragment>{isAuthenticated ? authLinks : guestLinks}</Fragment>
      )}
    </nav>
  );
};

Navbar.propTypes = {
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logout }
)(Navbar);
